	var updated_rows=""
	function updated(event)
		{
			updated_rows+="$$"+event.target.parentNode.parentNode.id;

		var arrayss=updated_rows;
			arrayss=arrayss.split("$$");
			arrayss = arrayss.filter(function(n){ return n != "" });
			arrayss = unique( arrayss );
			arrayss=arrayss.toString();
			document.getElementById("update_ids").value=arrayss;
		}

	function unique(list) {
    var result = [];
    $.each(list, function(i, e) {
        if ($.inArray(e, result) == -1) result.push(e);
    });
    return result;
		}

